export const createQuiz = (req, resp) => {
  //quiz create:...

  const { quizId, title, description } = req.body;
  // console.log(quizId);
  // console.log(title);
  // console.log(description);
  const quiz = {
    quizId,
    title,
    description,
  };

  quizzes.push(quiz);

  return resp.send("quiz created");
};

// get single quiz
export const getSingleQuiz = (req, resp) => {
  const { quizId } = req.params;
  console.log(quizId);

  const resultQuiz = quizzes.find((quiz) => quiz.quizId == quizId);

  return resp.json(resultQuiz);
};

//delete and update
export const deleteQuiz = (req, resp) => {
  const { quizId } = req.params;

  const newQuizzes = quizzes.filter((quiz) => quiz.quizId != quizId);
  quizzes = newQuizzes;
  return resp.send("quiz is deleted !!");
};
